levels[5] = {
  chapters = {
    { story = "D’abord le vent, et maintenant la mer ? Et quoi après, la Bretagne toute entière veut sa peau ? Ok, du calme, c’est le moment de reprendre mes recherches … Facebook : rien de neuf. Twitter : que dalle. Les mails … La photo d’un bateau ? « Voilier Saint-Joseph, confirmation de votre achat. Comme convenu, votre nouvelle acquisition vous attend au port de mouillage de Saint-Malo. » J’emporte le coquillage et je fonce dans la voiture. Il faut que j’y sois avant elle ! Le GPS affichait quatre heures de route, j’y suis en trois heures. Je suis sur le port. Il fait nuit mais j’en suis certain : le bateau est là devant moi, flambant neuf. Sur sa coque rouge est écrit en blanc : « Le buccin ». Quelle ironie. Je siphonne l’essence de ma voiture et en arrose la coque. Une allumette et tout est réglé.",
      illustration = "resources/images/boat.png",
      sound = "resources/sounds/BurningBoat.wav"
    },
    { story = "Jeudi 15 mars\n\nTous les journaux ont la même information en une : « Des rafales de 150 km/h annoncées dans l’Ouest » ; « Tempête subite en Bretagne » ; « Ouest : 15 départements en vigilance orange. », … J’allume la télé, où le sujet revient en boucle. « Les spécialistes eux-mêmes ont été surpris par l’irruption d’événements météorologiques si exceptionnels, nous ne comprenons pas pour quelles raisons cette tempête a pu être ignorée par nos prévisions. ». C’était moins une. Je n’ai pas eu le courage d’appeler Emma après l’incendie du bateau, encore moins d’aller la voir. Je ne sais pas quand je trouverai la force de le faire.",
      illustration = "resources/images/shell.png"
    },
    { story = "En attendant, drôle de coquillage, je ne sais toujours pas pourquoi tu m’es venu en aide. Es-tu doué de ta propre volonté ? Es-tu l’instrument de transmission de ma bonne étoile ? Je vais te reposer sur la plage où je t’ai trouvé, en espérant que tu puisses aider à son tour le prochain curieux qui te ramassera. Merci, coquillage.",
      illustration = "resources/images/shell.png",
      sound = "resources/sounds/Beach.wav"
    }
  },
  sentence = "",
